<div class="max-w-2xl mx-auto p-8 bg-gradient-to-br from-purple-600 to-blue-500 rounded-2xl shadow-xl">
    <h2 class="text-3xl font-bold text-white mb-8 text-center">Get in Touch</h2>
    <form class="space-y-6">
        <div class="relative">
            <input type="text" class="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-transparent peer focus:outline-none focus:border-white/50" placeholder="Full Name">
            <label class="absolute left-4 -top-2.5 px-1 text-sm text-white/80 bg-gradient-to-br from-purple-600 to-blue-500 transition-all duration-200 peer-placeholder-shown:text-base peer-placeholder-shown:text-white/60 peer-placeholder-shown:top-3 peer-focus:-top-2.5 peer-focus:text-sm">Full Name</label>
        </div>
        <div class="relative">
            <input type="email" class="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-transparent peer focus:outline-none focus:border-white/50" placeholder="Email Address">
            <label class="absolute left-4 -top-2.5 px-1 text-sm text-white/80 bg-gradient-to-br from-purple-600 to-blue-500 transition-all duration-200 peer-placeholder-shown:text-base peer-placeholder-shown:text-white/60 peer-placeholder-shown:top-3 peer-focus:-top-2.5 peer-focus:text-sm">Email Address</label>
        </div>
        <div class="relative">
            <textarea rows="4" class="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-transparent peer focus:outline-none focus:border-white/50" placeholder="Your Message"></textarea>
            <label class="absolute left-4 -top-2.5 px-1 text-sm text-white/80 bg-gradient-to-br from-purple-600 to-blue-500 transition-all duration-200 peer-placeholder-shown:text-base peer-placeholder-shown:text-white/60 peer-placeholder-shown:top-3 peer-focus:-top-2.5 peer-focus:text-sm">Your Message</label>
        </div>
        <button type="submit" class="w-full bg-white text-purple-600 py-3 px-6 rounded-lg font-semibold hover:bg-white/90 transition-colors duration-300">
            Send Message
        </button>
    </form>
</div>
